import { PriorityQueueNodeInterface } from "./priority-queue-node.interface";

/**
 * PriorityQueueNode
 * 
 * PriorityQueueNode is a single node in a priority queue.
 */

export class PriorityQueueNode<T> implements PriorityQueueNodeInterface<T> {

    private readonly _priority: number;
    private readonly _value: T;

    constructor(value: T, priority: number = 1) {
        this._priority = priority;
        this._value = value;
    }

    /**
     * priority()
     * 
     * priority() gets the priority of the node.
     */

    public priority(): number {
        return this._priority;
    }

    /**
     * value()
     * 
     * value() gets the value of the node.
     */

    public value(): T {
        return this._value;
    }
}