/**
 * Node
 * 
 * Node represents a generic data container.
 */

export interface StackNodeInterface<T> {

    /**
     * next()
     * 
     * next() gets the next node.
     */

    next(): StackNodeInterface<T> | null;

    /**
     * value()
     * 
     * value() gets the value the node is holding.
     */
    value(): T;
}