import { EmailAddress } from './../../src/email/email.module';
import { InvalidArgumentException } from '../../src/common/common.module';

test("Creating a Valid Email Address.", () => {

    const email = new EmailAddress("patrickaluy@gmail.com");
    expect(email).toBeDefined();
});

test("Creating an invalid email should throw an exception", () => {
    expect(() => new EmailAddress("")).toThrow(InvalidArgumentException);
    expect(() => new EmailAddress("Foo")).toThrow(InvalidArgumentException);
});